import React from 'react';
import { Link } from 'react-router-dom';
import 'isomorphic-fetch';
import Footer from '../Common/Footer';
 
class Contact extends React.Component {
    state = {
        submitting: false,
        submitted: false,
        buttonState: '',
        formFields: {
            name: '',
            email: '',
            phone: '',
            text: ''
        }
    };

    onSubmit = (e) => {
        e.preventDefault();
        const data = this.state.formFields;
        fetch('/api/contact', {
            method: 'post',
            headers: {
                'Accept': 'application/json, text/plain, */*',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(data)
        }).then(res => {
            if (res.status === 200) {
                this.setState({ submitted: true })
            }
            let formFields = Object.assign({}, this.state.formFields);
            formFields.name = '';
            formFields.email = '';
            formFields.phone = '';
            formFields.text = '';
            this.setState({formFields});
        });
    }

    nameChangeHandler = (e) => {
        let formFields = Object.assign({}, this.state.formFields);
        formFields.name = e.target.value;
        this.setState({formFields});
    }

    emailChangeHandler = (e) => {
        let formFields = Object.assign({}, this.state.formFields);
        formFields.email = e.target.value;
        this.setState({formFields});
    }

    phoneChangeHandler = (e) => {
        let formFields = Object.assign({}, this.state.formFields);
        formFields.phone = e.target.value;
        this.setState({formFields});
    }

    textChangeHandler = (e) => {
        let formFields = Object.assign({}, this.state.formFields);
        formFields.text = e.target.value;
        this.setState({formFields});
    }

    onHideSuccess = () => {
        this.setState({submitted: false});
    }

    successMessage = () => {
        if (this.state.submitted){
            return (
                <div 
                    className="alert alert-success alert-dismissible fade show" 
                    style={{ marginTop: '14px' }}
                >
                    <strong>Thank you!</strong> Your message is send to the owner.
                    <button 
                        type="button" 
                        className="close"
                        onClick={this.onHideSuccess}
                    >
                        <span aria-hidden="true">&times;</span>
                    </button>
                </div>
            );
        }
    }

    render(){
        return (
            <React.Fragment>
                <div className="page-title-area item-bg1">
                    <div className="container">
                        <h1>Contact Us</h1>
                        <span>Send us your enquiry</span>
                        <ul>
                            <li><Link to="/">Home</Link></li>
                            <li>Contact</li>
                        </ul>
                    </div>
                </div>

                <section className="contact-area ptb-120">
                    <div className="container">
                        <div className="row" >
                            <div className="col-lg-4 col-md-6"  style={{
                                display:'flex',
                                justifyContent: "left",
                            }}>
                                <div className="contact-box" >
                                    <div className="icon" >
                                        <i className="icofont-phone"></i>
                                    </div>
                                    
                                    <div className="content" style={{
                                        textAlign: 'left',
                                    }}>
                                        <h4>Phone / mobile no.</h4>
                                        <p>(+021) 245522455</p>
                                        <p>(+000) 245522455</p>
                                    </div>
                                </div>
                            </div>
                            
                            <div className="col-lg-4 col-md-6"  style={{
                                display:'flex',
                                justifyContent: "left",
                            }}>
                                <div className="contact-box" style={{
                                    marginLeft: '100px',
                                }}>
                                    <div className="icon">
                                        <i className="icofont-email"></i>
                                    </div>
                                    
                                    <div className="content" style={{
                                        textAlign: 'left',
                                    }}>
                                        <h4>E-mail</h4>
                                        <p>info@evnia.com</p>
                                        <p>support@evnia.com</p>
                                    </div>
                                </div>
                            </div>
                            
                            <div className="col-lg-4 col-md-6 offset-lg-0 offset-md-3"  style={{
                                display:'flex',
                                justifyContent: "left",
                               
                            }} >
                                <div className="contact-box" >
                                    <div className="icon" >
                                        <i className="icofont-world" ></i>
                                    </div>
                                    
                                    <div className="content" style={{
                                        textAlign: 'left',
                                    }}>
                                        <h4>Location</h4>
                                        <p>No. 124, Paud Rd, Mahatma Society, Kothrud, Pune, Maharashtra 411029</p>
                                    </div>
                                </div>
                            </div>
                        </div>

                        <div className="row h-100 align-items-center contact-form">
                            <div className="col-lg-4 col-md-6"style={{
                                marginTop: "-50px"
                            }}>
                                <div className="leave-your-message">   
                                    <div className="stay-connected" style={{
                                        textAlign: 'left',
                                       
                                    }}>
                                        <h3 style={{
                                            paddingBottom: '20px'
                                        }}>Stay Connected</h3>
                                        <ul style={{
                                            display: 'flex',
                                            justifyContent:'flex-start'
                                        }}>
                                            <li>
                                                <Link to="#" target="_blank">
                                                    <i className="icofont-facebook"></i>
                                                    <span>Facebook</span>
                                                </Link>
                                            </li>
                                            
                                            <li>
                                                <Link to="#" target="_blank">
                                                    <i className="icofont-twitter"></i>
                                                    <span>Twitter</span>
                                                </Link>
                                            </li>
                                            
                                            <li>
                                                <Link to="#" target="_blank">
                                                    <i className="icofont-instagram"></i>
                                                    <span>Instagram</span>
                                                </Link>
                                            </li>
                                            
                                            <li>
                                                <Link to="#" target="_blank">
                                                    <i className="icofont-linkedin"></i>
                                                    <span>Linkedin</span>
                                                </Link>
                                            </li>
                                        </ul>
                                    </div>
                                </div>
                            </div>

                            <div className="col-lg-4 col-md-6" style={{
                                marginRight: '420px',
                            }}>
                                <div className="leave-your-message" style={{
                                    textAlign: 'left',
                                }}>
                                    <h3>Leave Your Message</h3>
                                    <p>If you have any questions about the services we provide simply use the form below. 
                                        We try and respond to all queries and comments within 24 hours.</p>
                                </div>
